        if (arr.isEmpty()) return true;
        int first = 0;
        int last = arr.size() - 1;
        while (first < last) {
            // search the next non-ascending item in the sorted tail
            int next = first;
            while (++next < arr.size() && arr.get(first) <= arr.get(next)) ;
            if (next < arr.size()) {
