        // write your code here
        ArrayList<Long> temp = new ArrayList<Long>();
        int i = 1;
        for(long x : xs)
        {
            temp.add(i * x);
            i++;
        }
        return temp.stream().skip(1).collect(Collectors.toCollection(ArrayList::new));

