    // (false)
    // >>> isSorted((new ArrayList<Long>(Arrays.asList((long)1l, (long)2l, (long)2l, (long)3l, (long)3l, (long)4l))))
    // (true)
    // >>> isSorted((new ArrayList<Long>(Arrays.asList((long)1l, (long)2l, (long)2l, (long)2l, (long)3l, (long)4l))))
    // (false)
    public static boolean isSorted(ArrayList<Long> lst) {
        // Ignore the trivial case where the array list is empty or has only one element
        if(lst.size() < 2) return true;
        // We can not have a duplicate if the length of the array list is less than the
        // number of unique values of the array list
        if(lst.size() < new HashSet<Long>(lst).size()) return false;
        // Get an iterator to the array list and compare each element to the previous
        // element. If the element is not bigger than the previous, return false.
        Iterator<Long> iterator = lst.iterator();
        Long last = iterator.next();
        while(iterator.hasNext()) {
            Long current = iterator.next();
            if(current <= last) return false;
            last = current;
        }
        // If we get here, the array list must be sorted
        return true;

