        boolean isSorted = true;
        boolean hasDuplicate = false;
        long lastNum = lst.get(0);
        while(index < lst.size()) {
            long currentNum = lst.get(index);
            if(currentNum < lastNum) {
                isSorted = false;
            } else if(currentNum == lastNum) {
                if(lastNumWasEqual) {
                    hasDuplicate = true;
                }
                lastNumWasEqual = true;
            } else {
                lastNumWasEqual = false;
            }
            lastNum = currentNum;
            index++;
        }
