class Problem {
    // Given a positive integer N, return the total sum of its digits in binary.
    // Example
    // >>> solve((1000l))
    // ("1")
    // >>> solve((150l))
    // ("110")
    // >>> solve((147l))
    // ("1100")
    // Variables:
    // @N integer
    // Constraints: 0 ≤ N ≤ 10000.
    // Output:
    // a string of binary number
    public static String solve(long N) {
        String bin = Long.toBinaryString(N);
        int count = 0;
        int count1 = 0;
        int totalSum = 0;
        for (int i = bin.length() - 1; i >= 0; i--) {
            char c = bin.charAt(i);
            if (c == '1') {
                count1++;
                count++;
            } else if (c == '0') {
                count1++;
            }
            if (count == 4) {
                totalSum += count1;
                count = 0;
                count1 = 0;
            }
        }
        if (count1 > 0 && count1 != bin.length()) {
            totalSum += count1;
        }
        return String.valueOf(totalSum);

