    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> maxHeap = new ArrayList<Long>();
        for (Long i : arr) {
            if (maxHeap.size() < k) {
                maxHeap.add(i);
            } else {
                if (maxHeap.get(0) < i) {
                    maxHeap.add(i);
                    maxHeap.remove(0);
                }
            }
        }
        return maxHeap;

