    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> a = new ArrayList<>();
        for (int i = 0; i < k; i++) {
            a.add((long)0);
        }
        for (long n : arr) {
            for (int i = 0; i < a.size(); i++) {
                if (n > a.get(i)) {
                    if (i == a.size() - 1) {
                        a.set(i, n);
                    }
                    else if (a.get(i) == a.get(i + 1)) {
                        if (i == a.size() - 2) {
                            a.set(i, n);
                        }
                        else if (a.get(i + 1) == a.get(i + 2)) {
                            if (i == a.size() - 3) {
                                a.set(i, n);
                            }
                        }
                        else {
                            a.set(i, n);
                        }
                    }
                    else {
                        a.set(i, n);
                    }
                    break;
                }
            }
        }
        return a;

