    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> sortedArr = new ArrayList<Long>();
        for (int i = 0; i < arr.size(); i++) {
            if (sortedArr.isEmpty()) {
                sortedArr.add(arr.get(i));
            } else {
                int j = 0;
                for (j = 0; j < sortedArr.size(); j++) {
                    if (arr.get(i) >= sortedArr.get(j)) {
                        break;
                    }
                }
                sortedArr.add((j), arr.get(i));
                if (sortedArr.size() > k) {
                    sortedArr.remove(k);
                }
            }
        }
        return sortedArr;

