    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> out = new ArrayList<Long>();
        while(k > 0) {
            long currMax = Long.MIN_VALUE;
            for(long n : arr) {
                if(n > currMax) {
                    currMax = n;
                }
            }
            arr.remove((long)arr.indexOf((long)currMax));
            out.add(currMax);
            k--;
        }
        return out;

