    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> result = new ArrayList<Long>();
        int m = arr.size() - k;
        for (int i = 0; i < k; i++)
            result.add(Long.valueOf(-1001));
        for (long i : arr) {
            int ind = 0;
            while (ind < k) {
                if (result.get(ind).longValue() < i) {
                    for (int j = k - 1; j > ind; j--)
                        result.set(j, result.get(j - 1));
                    result.set(ind, Long.valueOf(i));
                    break;
                }
                ind++;
            }
            if (ind == k) {
                if (result.get(m).longValue() < i) {
