                        if (result.get(j).longValue() < i) {
                            for (int l = m; l > j; l--)
                                result.set(l, result.get(l - 1));
                            result.set(j, Long.valueOf(i));
                            break;
                        }
                    }
                }
            }
        }
        return result;

