    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> result = new ArrayList<>();
        for (Long i : arr) {
            int resultIndex = 0;
            while (result.size() > resultIndex && result.get(resultIndex) < i) {
                resultIndex++;
            }
            result.add(resultIndex, i);
        }
        return result.stream().limit(k).collect(Collectors.toCollection(ArrayList::new));

