        ArrayList<Long> result = new ArrayList<Long>();
        for (int i = 0; i < arr.size(); i++) {
            if (result.size() < k)
                result.add(arr.get(i));
            else if (result.get(result.size() - 1) < arr.get(i)) {
                result.set(result.size() - 1, arr.get(i));
                result.sort((a, b) -> { return (int)(b - a); });
            }
        }
        return result;

