    // 2. The elements in the array array list will be in the range of [-1000, 1000].
    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> res = new ArrayList<>();
        for(Long x : arr) {
            res.add(x);
        }
        long count = k;
        for(long i = res.size() - 1; i > 0 && count > 0; --i) {
            long max = Long.MIN_VALUE;
            int index = -1;
            for(int j = 0; j <= i; ++j) {
                if(res.get(j) > max) {
                    max = res.get(j);
                    index = j;
                }
            }
            res.set(index, res.get(i));
            res.set(i, max);
            --count;
        }
        return res;

