    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> output = new ArrayList<>();
        for (long n : arr) {
            int maxLength = output.size() >= k ? k : output.size() + 1;
            if (output.size() < maxLength) {
                output.add(n);
                continue;
            }
            int index = maxLength - 1;
            while (output.get(index) <= n && index >= 0) {
                index--;
            }
            if (index == -1) {
                continue;
            }
            output.set(index, n);
        }
        return output;

