        class Pair {
            Long value;
            int index;
            Pair(Long value, int index) {
                this.value = value;
                this.index = index;
            }
        }
        ArrayList<Pair> list = new ArrayList<>();
        for (int i = 0; i < arr.size(); i++) {
            list.add(new Pair(arr.get(i), i));
        }
        list.sort((p1, p2) -> {
            return p2.value.compareTo(p1.value);
        });
        ArrayList<Long> res = new ArrayList<>();
        for (int i = 0; i < k; i++) {
