            for(int j = i + 1; j < result.size(); j++) {
                if(i % 2 == 0 && (array.get(i) + array.get(j)) % 2 == 1) {
                    if(array.get(i) > array.get(j)) {
                        result.set(j, array.get(i));
                        result.set(i, array.get(j));
                    }
                } else if(i % 2 == 0 && (array.get(i) + array.get(j)) % 2 == 0) {
                    if(array.get(i) < array.get(j)) {
                        result.set(j, array.get(i));
                        result.set(i, array.get(j));
                    }
                } else if(i % 2 != 0 && (array.get(i) + array.get(j)) % 2 == 0) {
                    if(array.get(i) > array.get(j)) {
                        result.set(j, array.get(i));
                        result.set(i, array.get(j));
                    }
                } else if(i % 2 != 0 && (array.get(i) + array.get(j)) % 2 == 1) {
