    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        if(array.size() <= 1) return array;
        ArrayList<Long> left = new ArrayList<Long>();
        ArrayList<Long> right = new ArrayList<Long>();
        for(long num : array){
            if(sum(array.get(0), num) % 2 == 0){
                left.add(num);
            }else{
                right.add(num);
            }
        }
        ArrayList<Long> res = new ArrayList<Long>();
        if(sum(array.get(0), array.get(0)) % 2 == 0){
            res.addAll(right);
            res.addAll(left);
        }else{
            res.addAll(left);
            res.addAll(right);
        }
        return res;

