        ArrayList<Long> sorted = new ArrayList<Long>();
        for (int i = 0; i < array.size(); i++) {
            sorted.add(array.get(i));
        }
        int j = 0;
        int i = 0;
        while (j < sorted.size() && i < sorted.size()) {
            long sum = sorted.get(j) + sorted.get(i);
            if (sum % 2 == 0) {
                if (i != j) {
                    Long tmp = sorted.get(i);
                    sorted.set(i, sorted.get(j));
                    sorted.set(j, tmp);
                }
                i++;
                j++;
            } else {
                j++;
            }
        }
        return sorted;

