        List<Integer> oddIndexes = new ArrayList<>();
        List<Integer> evenIndexes = new ArrayList<>();
        for (int i = 0; i < sortedArray.size(); i++) {
            if ((i + 1) % 2 == 0) {
                evenIndexes.add(i);
            } else {
                oddIndexes.add(i);
            }
        }
        Collections.sort(sortedArray, (o1, o2) -> {
            if (sortedArray.get(o1) > sortedArray.get(o2)) {
                return 1;
            } else if (sortedArray.get(o1) < sortedArray.get(o2)) {
                return -1;
            }
            return 0;
        });
        Collections.sort(evenIndexes, (o1, o2) -> {
            if (sortedArray.get(o1) > sortedArray.get(o2)) {
                return 1;
            } else if (sortedArray.get(o1) < sortedArray.get(o2)) {
