                    if (array.get(i) + array.get(i - 1) % 2 == 0) {
                        even.add(array.get(i));
                    } else {
                        odd.add(array.get(i));
                    }
                } else {
                    if (array.get(i) + array.get(i - 1) % 2 == 0) {
                        odd.add(array.get(i));
                    } else {
                        even.add(array.get(i));
                    }
                }
            }
        }
        if (even.size() != 0) {
            Collections.sort(even);
