                for (long j = 1; j <= i; j++) {
                    fac *= j;
                }
                res.add(fac);
            } else {
                long sum = 0;
                for (long j = 1; j <= i; j++) {
                    sum += j;
                }
                res.add(sum);
            }
        }
        return res;

