    // (true)
    // >>> pairsSumToZero((new ArrayList<Long>(Arrays.asList((long)1l))))
    // (false)
    public static boolean pairsSumToZero(ArrayList<Long> l) {
        // will store the number of occurences of each integer.
        HashMap<Long, Long> count = new HashMap<Long, Long>();
        for (long i : l) {
            count.put(i, 1L);
        }
        // will store the sum of the occurences of each integer
        HashMap<Long, Long> sum = new HashMap<Long, Long>();
        for (long i : l) {
            sum.put(i, count.get(i));
            for (long j : l) {
                if (i != j) {
                    sum.put(i, sum.get(i) + count.get(j));
                }
            }
        }
        // if there is any key whose value is 2, then there is a pair summing to 0.
        for (long i : l) {
            if (sum.get(i) > 1) {
                return true;
            }
        }
