                encrypted.append(c);
                continue;
            }
            int shifted = c + (2 * 2);
            if (shifted > 'z') {
                encrypted.append((char)('a' + (shifted - 'z' - 1)));
            } else {
                encrypted.append((char)shifted);
            }
        }
        return encrypted.toString();

