        if (dict.isEmpty())
            return false;
        char[] ch = dict.keySet().toArray(new String[dict.size()])[0].toCharArray();
        int i = 0, j = 0;
        while (ch[i++] != '\0')
            if (Character.isUpperCase(ch[i]))
                j++;
            else if (Character.isLowerCase(ch[i]))
                j--;
        return j == -1 || j == 0;

