        int count = 0;
        // O(n^2)
        for (int i = 0; i < arr.size(); i++) {
            if (i == arr.size() - 1) {
                continue;
            }
            int tmp = i;
            while (tmp < arr.size() - 1 && arr.get(tmp) < arr.get(tmp + 1)) {
                tmp++;
            }
            if (i == tmp) {
                count++;
            }
        }
        return count == 1;

