                int tmp = 0;
                for (int j = 0; j < l / 2 + 1; j++) {
                    if (s.charAt(j) == s.charAt(l - 1 - j)) {
                        tmp++;
                    } else {
                        break;
                    }
                }
                if (tmp == l / 2 + 1) {
                    odd++;
                }
            }
        }
        return Pair.with(even, odd);

