    // Note:
    // 1. 1 <= n <= 10^3
    // 2. returned pair has the number of even and odd integer palindromes respectively.
    public static Pair<Long, Long> evenOddPalindrome(long n) {
        int count = 0;
        ArrayList<Long> odd = new ArrayList<Long>();
        ArrayList<Long> even = new ArrayList<Long>();
        for (long i = 1; i <= n; i++) {
            if (isOdd(i))
                odd.add(i);
            else
                even.add(i);
        }
        for (long i = 0; i < odd.size(); i++) {
            long first = odd.get(i);
            for (long j = i + 1; j < odd.size(); j++) {
                long second = odd.get(j);
                if (first == second)
                    count++;
                else if (first < second) {
                    long temp = first;
                    first = second;
                    second = temp;
                    if (isPalindrome(first + second))
                        count++;
                }
            }
        }
        for (long i = 0; i < even.size(); i++) {
            long first = even.get(i);
            for (long j = i + 1; j < even.size(); j++) {
                long second = even.get(j);
                if (first == second)
                    count++;
                else if (first < second) {
                    long temp = first;
                    first = second;
                    second = temp;
                    if (isPalindrome(first + second))
                        count++;
                    if (isPalindrome(first - second))
                        count++;
                }
            }
        }
        return Pair.with(count / 2, count / 2 + count % 2);

