                long j = reverse(i);
                if (j > 0 && j <= n && i == j) ++evenPalindrome;
            } else {
                long j = reverse(i);
                if (j > 0 && j <= n && i + j == n) ++oddPalindrome;
            }
        }
        return Pair.with(evenPalindrome, oddPalindrome);

