    public static Pair<Long, Long> evenOddPalindrome(long n) {
        if (n <= 1000) {
            // for n <= 1000, there are at most 1001 even numbers and 1001 odd numbers.
            // so there are at most 2002 palindrome numbers.
            int count_even = 0;
            int count_odd = 0;
            for (long i = 1; i <= n; ++i) {
                int tmp = (int) i;
                int reverse = 0;
                while (tmp > 0) {
                    reverse *= 10;
                    reverse += tmp % 10;
                    tmp /= 10;
                }
                if (reverse == i) {
                    if ((reverse & 1) == 0)
                        count_even++;
                    else
                        count_odd++;
                }
            }
            return Pair.with(Long.valueOf(count_even), Long.valueOf(count_odd));
        }
        // n > 1000
        // find even palindromes.
        int[] count = new int[5002];
        for (int i = 2; i <= 5000; ++i) {
            count[(i * i) / 2 + i / 2]++;
        }
        // find odd palindromes.
        for (int i = 1; i <= 5000; ++i) {
            count[(i * i + i) / 2 - i / 2]++;
        }
        int[] res = new int[2];
        for (int i = 0; i <= n / 2; ++i) {
            res[0] += count[i];
            res[1] += count[n - i];
        }
        return Pair.with(Long.valueOf(res[0]), Long.valueOf(res[1]));

