        }
        for (int i = (int)n-1; i >= 0; --i) {
            if (i > 9) {
                p[i] += p[i/10];
            }
            p[i] += p[i%10];
            if (i < 99) {
                p[i] += p[i/100];
            }
            p[i] += p[i%10];
        }
        return Pair.with(p[n]/2, (n/2+n%2)*2-p[n]);

