class Problem {
    // xs represent coefficients of a polynomial.
    // xs[0] + xs[1] * x + xs[2] * x^2 + ....
    // Return derivative of this polynomial in the same form.
    // >>> derivative((new ArrayList<Long>(Arrays.asList((long)3l, (long)1l, (long)2l, (long)4l, (long)5l))))
    // (new ArrayList<Long>(Arrays.asList((long)1l, (long)4l, (long)12l, (long)20l)))
    // >>> derivative((new ArrayList<Long>(Arrays.asList((long)1l, (long)2l, (long)3l))))
    // (new ArrayList<Long>(Arrays.asList((long)2l, (long)6l)))
    public static ArrayList<Long> derivative(ArrayList<Long> xs) {
        int n = xs.size();
        if(n <= 1) return new ArrayList<Long>();
        // 1. create new ArrayList<Long>(n - 1) to store result.
        // 2. iterate xs[0] ~ xs[n - 1]
        //    a. if i is 0, xs[0] * 0 + xs[1] * 1 = xs[1]
        //    b. else, xs[i - 1] * (i - 1) + xs[i] * i
        // 3. return new ArrayList<Long>(n - 1)
        ArrayList<Long> result = new ArrayList<Long>(n - 1);
        for(int i = 0; i < n; i++) {
            if(i == 0) {
                result.add(0l);
            } else {
                result.add((i - 1) * xs.get(i - 1) + i * xs.get(i));
            }
        }
        return result;

