        ArrayList<Long> ys = new ArrayList<Long>();
        while (j < xs.size()) {
            ys.add((long)j);
            ys.add(xs.get(j));
            j++;
        }
        for (i = 0; i < xs.size(); i++) {
            ys.add((long)i);
            ys.add(xs.get(i) * (ys.size() - i - 1));
        }
        for (i = 0; i < ys.size(); i++) {
            ys.set(i, ys.get(i) / ys.get(ys.size() - 1));
        }
        return ys;

