        if(flag == false)
            return new StringBuilder(s).reverse().toString();
        for(int i = 0; i < c.length; i++)
            if(Character.isLetter(c[i]))
                c[i] = Character.isUpperCase(c[i]) ? Character.toLowerCase(c[i]) : Character.toUpperCase(c[i]);
