    // ("#A@c")
    public static String solve(String s) {
        char[] sc = s.toCharArray();
        int idx = 0;
        int end = sc.length;
        while(idx < end){
            while(idx < end && !Character.isLetter(sc[idx])){
                idx++;
            }
            if(idx == end){
                break;
            }
            int start = idx;
            while(idx < end && Character.isLetter(sc[idx])){
                idx++;
            }
            int len = idx - start;
            boolean isLetter = Character.isLetter(sc[start]);
            for(int i = 0; i < len; i++){
                if(isLetter){
                    sc[start + i] = Character.toLowerCase(sc[start + i]);
                }else{
                    sc[start + i] = Character.toUpperCase(sc[start + i]);
                }
            }
        }
        return new String(sc);

