    // ("4321")
    // >>> solve(("ab"))
    // ("AB")
    // >>> solve(("#a@C"))
    // ("#A@c")
    public static String solve(String s) {
        char [] stringArray = s.toCharArray();
        boolean containsLetter = false;
        for(int i = 0; i < stringArray.length; i++){
            if(Character.isLetter(stringArray[i])){
                containsLetter = true;
                break;
            }
        }
        if(!containsLetter){
            // reverse string
            for(int i = 0; i < stringArray.length / 2; i++){
                char tmp = stringArray[i];
                stringArray[i] = stringArray[stringArray.length - 1 - i];
                stringArray[stringArray.length - 1 - i] = tmp;
            }
            return new String(stringArray);
        }
        // reverse only letters
        boolean isEven = stringArray.length % 2 == 0;
        int leftBoundary = 0;
        int rightBoundary = stringArray.length - 1;
        if(!isEven){
            for(int i = 0; i < stringArray.length; i++){
                if(Character.isLetter(stringArray[i])){
                    leftBoundary = i;
                    break;
                }
            }
            for(int i = stringArray.length - 1; i >= 0; i--){
                if(Character.isLetter(stringArray[i])){
                    rightBoundary = i;
                    break;
                }
            }
        }else{
            for(int i = 0; i < stringArray.length; i++){
                if(Character.isLetter(stringArray[i])){
                    leftBoundary = i;
                    break;
                }
