    public static String solve(long N) {
        int sum = 0;
        while (N != 0) {
            int lastDigit = (int) N % 10;
            N /= 10;
            int binaryDigit = 0;
            while (lastDigit != 0) {
                binaryDigit += lastDigit % 2;
                lastDigit /= 2;
            }
            sum += binaryDigit;
        }
        return String.valueOf(sum);

