    // Given a positive integer N, return the total sum of its digits in binary.
    // Example
    // >>> solve((1000l))
    // ("1")
    // >>> solve((150l))
    // ("110")
    // >>> solve((147l))
    // ("1100")
    // Variables:
    // @N integer
    // Constraints: 0 ≤ N ≤ 10000.
    // Output:
    // a string of binary number
    public static String solve(long N) {
        // "N" is a long integer
        int bitCount = 0;
        // the bit size of "N"
        long temp = N;
        while (temp > 0) {
            temp = temp >> 1;
            bitCount++;
        }
        // count the bit size of "N"
        int[] result = new int[bitCount];
        for (int i = 0; i < bitCount; i++) {
            result[i] = 0;
        }
        // the array to store binary
        for (int i = 0; i < bitCount; i++) {
            temp = N;
            temp = temp >> i;
            if (temp % 2 == 0) {
                result[i] = 0;
            } else {
                result[i] = 1;
            }
        }
        String binary = "";
        for (int i = 0; i < bitCount; i++) {
            binary += result[i];
        }
        // convert "result" to a string
        return binary;

