            }
            return ((long) s.charAt(0) >= 65 && (s.charAt(0) <= 90 || s.charAt(0) >= 122)) + ((long) s.charAt(2) >= 65 && (s.charAt(2) <= 90 || s.charAt(2) >= 122)) + ((long) s.charAt(4) >= 65 && (s.charAt(4) <= 90 || s.charAt(4) >= 122)) ? 1 : 0;
        }).sum();

