    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> ret = new ArrayList<Long>();
        PriorityQueue<Long> heap = new PriorityQueue<Long>(new Comparator<Long>(){
            public int compare(Long a, Long b) {
                return (int)(a-b);
            }
        });
        for(long i: arr) {
            if(heap.size() < k) {
                heap.offer(i);
            } else {
                heap.offer(Math.max(heap.peek(), i));
            }
        }
        for(long i: heap) {
            ret.add(i);
        }
        return ret;

