    // 2. The elements in the array array list will be in the range of [-1000, 1000].
    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        int N = arr.size();
        if(k > N) return arr;
        int i = 0;
        for(int j = 0; j < k; j++) {
            long maxValue = Long.MIN_VALUE;
            while(i < N) {
                if(arr.get(i) > maxValue) {
                    maxValue = arr.get(i);
                    i++;
                } else {
                    i++;
                    break;
                }
            }
            arr.set(j, maxValue);
        }
        return arr;

