        int[] topK = new int[k];
        for (int i = 0; i < k; i++) {
            topK[i] = (int)(i);
        }
        for (int i = k; i < arr.size(); i++) {
            int minIndex = topK[0];
            for (int j = 1; j < k; j++) {
                if (arr.get(topK[j]) < arr.get(topK[minIndex])) {
                    minIndex = topK[j];
                }
            }
            if (arr.get(i) > arr.get(topK[minIndex])) {
                topK[minIndex] = (int)(i);
            }
        }
        ArrayList<Long> res = new ArrayList<Long>();
        for (int i : topK) {
            res.add(arr.get(i));
        }
        return res;

