    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        ArrayList<Long> result = new ArrayList<>();
        int i = arr.size() - 1;
        while(result.size() < k) {
            long max = Long.MIN_VALUE;
            int index = i;
            for(int j = i; j >= 0; j--) {
                if(arr.get(j) > max) {
                    max = arr.get(j);
                    index = j;
                }
            }
            result.add(max);
            arr.set(index, Long.MIN_VALUE);
            i--;
        }
        return result;

