    // 3. 0 <= k <= len(arr)
    public static ArrayList<Long> maximum(ArrayList<Long> arr, long k) {
        List<Long> results = new ArrayList<>(k);
        int start = 0, end = (int) k - 1;
        while (k > 0) {
            Long maxVal = arr.get(start);
            int maxIndex = start;
            for (int i = start + 1; i <= end; ++i) {
                if (arr.get(i) > maxVal) {
                    maxVal = arr.get(i);
                    maxIndex = i;
                }
            }
            results.add(maxVal);
            k--;
            if (maxIndex == start) {
                start++;
            } else if (maxIndex == end) {
                end--;
            } else {
                arr.set(maxIndex, arr.get(start));
                arr.set(start, maxVal);
                start++;
                end--;
            }
        }
        return results;

