        ArrayList<Long> result = new ArrayList<Long>();
        ArrayList<Long> all = new ArrayList<Long>(arr);
        for (int i = 0; i < k; i++) {
            int maxIdx = 0;
            for (int j = 1; j < all.size(); j++) {
                if (all.get(maxIdx) < all.get(j)) {
                    maxIdx = j;
                }
            }
            result.add(all.get(maxIdx));
            all.remove(maxIdx);
        }
        return result;

