        for (long i = 0; i < arr.size(); i++) {
            // We compare each element in arr with result.
            // If result < arr, we replace it.
            for (long j = 0; j < k; j++) {
                if (result.get(j) < arr.get(i)) {
                    result.set(j, arr.get(i));
                    break;
                }
