        ArrayList<Long> r = new ArrayList<Long>();
        for (long i = 0; i < k; i++) r.add(Long.MIN_VALUE);
        for (long i = 0; i < k; i++) for (long j = 0; j < arr.size(); j++) if (arr.get((int)j) > r.get((int)i)) r.set((int)i, arr.get((int)j));
        return r;

