        ArrayList<Long> maxList = new ArrayList<Long>();
        for(int i = 0; i < arr.size(); i++) {
            int j = 0;
            while(j < maxList.size() && maxList.get(j) > arr.get(i)) {
                j++;
            }
            if(j < k) {
                maxList.add(j, arr.get(i));
            }
        }
