        ArrayList<Long> arrayOdd = new ArrayList<Long>();
        for(long num : array) {
            if(sum(num, array.get(size-1)) % 2 == 0) arrayEven.add(num);
            else arrayOdd.add(num);
        }
        if(arrayOdd.size() > arrayEven.size()) {
            ArrayList<Long> temp = arrayEven;
