    public static ArrayList<Long> sortArray(ArrayList<Long> array) {
        ArrayList<Long> answer = new ArrayList<Long>(array);
        int index = 0;
        while(index < answer.size() - 1){
            if(answer.get(index) % 2 == 0 && answer.get(index) + answer.get(answer.size() - 1) % 2 == 0){
                Collections.swap(answer, index, answer.size() - 1);
                break;
            } else if(answer.get(index) % 2 == 1 && answer.get(index) + answer.get(answer.size() - 1) % 2 == 1){
                Collections.swap(answer, index, answer.size() - 1);
                break;
